CREATE TABLE PEOPLE(
    NAME                    VARCHAR(255)
    , FURIGANA              VARCHAR(255)
    , MAIL_ADDRESS          VARCHAR(255)
    , GENDER                VARCHAR(10)
    , BIRTHDAY              DATE
    , IS_MARRIED             BOOLEAN
    , BLOOD_TYPE            VARCHAR(2)
    , PREF_NAME             VARCHAR(255)
    , PHONE                 VARCHAR(40)
    , MOBILE_PHONE          VARCHAR(40)
    , MOBILE_PHONE_CAREER   VARCHAR(40)
    , EATING_STYLE          VARCHAR(255)
    , PRIMARY KEY(NAME)
);


